(async function () {
  const data = await fetch("./src/data.json");

  const res = await data.json();

  let employees = res;

  let selectedEmployeeId = employees[0].id;
  let selectedEmployee = employees[0];

  const employeeList = document.querySelector(".employees_names_list");

  const employeeInfo = document.querySelector(".employees_single_info");

  //Add Employee Logic
  const createEmployee = document.querySelector(".createEmployee"); // Button
  const addEmployeeModal = document.querySelector(".addEmployee"); // Form Container
  const addEmployeeForm = document.querySelector(".addEmployee_create"); // Form 

  createEmployee.addEventListener("click", () => {
    addEmployeeModal.style.display = "flex";
  });

  addEmployeeModal.addEventListener("click", (e) => {
    if (e.target.className === "addEmployee") {
      addEmployeeModal.style.display = "none";
    }
  });

  const dobInput = document.querySelector(".addEmployee_create--dob");
  dobInput.max = `${new Date().getFullYear() - 18}-${new Date()
    .toISOString() // year-month-date  2006-07-12
    .slice(5, 10)}`;



  addEmployeeForm.addEventListener("submit", (e) => {
    e.preventDefault();

    const formData = new FormData(addEmployeeForm);
    console.log("formdata", formData);
    const values = [...formData.entries()];

    console.log("values", values);

    let empData = {};

    values.forEach((item) => {
      empData[item[0]] = item[1];
    });

    empData.id = employees[employees.length - 1].id + 1;
    empData.age = new Date().getFullYear() - parseInt(empData.dob.slice(0, 4));
    empData.imageUrl =
      empData.imageUrl ||
      "data:image/png;base64,";
    employees.push(empData);
    renderEmployees();
    addEmployeeForm.reset();
    addEmployeeModal.style.display = "none";
  });

  // Select Employee Logic

  employeeList.addEventListener("click", (e) => {
    if (e.target.tagName === "SPAN" && selectedEmployeeId != e.target.id) {
      selectedEmployeeId = e.target.id;
      renderEmployees();
      renderSingleEmployee();
    }

    // deleting Employee

    if (e.target.tagName === "I") {
      employees = employees.filter(
        (emp) => String(emp.id) !== e.target.parentNode.id
      );
    }

    if (String(selectedEmployeeId) === e.target.parentNode.id) {
      selectedEmployeeId = employees[0]?.id|| -1;
      selectedEmployee = employees[0] || {};
      renderSingleEmployee();
    }

    renderEmployees();
  });

  // render all employee name list
  const renderEmployees = () => {
    employeeList.innerHTML = "";

    employees.forEach((emp) => {
      const employee = document.createElement("span");
      // Adding classname to span tag
      employee.classList.add("employees__name--item");
      
      // Adding selected keyword when we click the single employee
      if (parseInt(selectedEmployeeId, 10) === emp.id) {
        employee.classList.add("selected");
        selectedEmployee = emp;
      }

      employee.setAttribute("id", emp.id);

      employee.innerHTML = `${emp.firstName}  ${emp.lastName} <i class = "employeeDelete">‚ùå</i>`;
      employeeList.append(employee);
    });
  };

  // rendering single employee
  const renderSingleEmployee = () => {
    if (selectedEmployeeId === -1) {
      employeeInfo.innerHTML = "";
      return;
    }

    employeeInfo.innerHTML = `
         <img src = "${selectedEmployee.imageUrl}" />
         <span class = "employees__single--heading">
         ${selectedEmployee.firstName} ${selectedEmployee.lastName} (${selectedEmployee.age})
         </span>
         <span>${selectedEmployee.address}</span>
         <span>${selectedEmployee.email}</span>
         <span>Mobile - ${selectedEmployee.contactNumber}</span>
         <span>DOB - ${selectedEmployee.dob}</span>
        `;
  };


  if (selectedEmployee) renderSingleEmployee();
  renderEmployees();
})();
